import React, { Component } from 'react';
import './index.scss';
import '../../commen.scss';
import TopBar from './components/TopBar'
import PosterSlide from './components/Slide'
import MovieItem from './components/MovieItem'
import CityLayer from './components/CityLayer'
import TabMenu from '../../components/TabMenu'
import RenderToBody from '../../components/RenderToBody'
import request from '../../helpers/request'

class Home extends Component {

  state = {
  	city: '',
  	poster: [],
  	movie: [],
  	cityLayerVisible: false
  }

  showCitylayer = () => {
  		alert('城市');
	}
  componentWillMount() {
  	this.getData();
  }

  getData = async () => {
  	const data = await request('/index');
  	console.log(data);
  	const {city, poster, movie} = data;
  	this.setState({
  		city,
  		poster,
  		movie
  	})

  }

  render() {
  	const {city, poster, movie} = this.state;
    return (
      <div className="home">
      		{/*主页顶部*/}
        	<TopBar city= {city} showCitylayer={this.showCitylayer}/>

			{/*主页顶部*/}
        	<div className="home-slide">
        		<div className="home-slideWrap">
        			<PosterSlide data={poster} />
        		</div>
        	</div>

            {/*电影列表*/}
        	<ul className="home-content">
        	{
        		movie.map(items => { 
        			return (
        				<li key="items.image"><MovieItem data={items} /></li>
        				)
        			
        		})
        	}
        		
        	</ul>

			{/*主页底部tab*/}
        	<TabMenu current={'movie'}/>

        	{/* <RenderToBody><CityLayer /></RenderToBody> */}
        	
      </div>
    );
  }
}

export default Home;
